import {LOGIN_SUCCESS, INVALID_LOGIN, LOGIN_NETWORK_ERROR, 
    COMPLETE_ONBOARDING, LOGOUT, POPULATE_SEARCH, SAVE_LISTING,SORT_BY_POPULARITY} from "./actionConstants";
import {login, getPreferences, saveUserListing,getListings, listingsByPopularity,getUserSavedListings,userCompletedOnboarding, addPreferenceForUser, removePreferenceForUser} from "../data/data";
import store from "./store";

export const loginSuccess = user => ({
    type: LOGIN_SUCCESS,
    payload: {
        user: user
    }
});

export const loginFail = () => ({
    type: INVALID_LOGIN
});

export const loginNetworkError = () => ({
    type: LOGIN_NETWORK_ERROR
});

/** The following actions get/update mock data and would have to be refactored
 * if/when the data source is changed.
 */

 // Eventually this action creator would be re-written to connect to a real data source
export const validateUser = (username, password) => {
    let user = login(username, password);
    if (user.id)
        return loginSuccess(user);
    else return loginFail();
    // No case for a network error yet
}

export const getSavedListings = () => {
    // Gets the current user from the store. Note the store import above.
    const userId = store.getState().user.id;
    return {
        type: SAVE_LISTING,
        payload: {
            saved: getUserSavedListings(userId)
        }
    }
}

// Note for future: need to handle error from data source
export const getUserListings = () => {
    // Gets the current user from the store. Note the store import above.
    const userId = store.getState().user.id;
    const listings = getListings(userId);

    return {
        type: POPULATE_SEARCH,
        payload: {
            listings: listings
        }
    }
}

export const getPopularityListing = () => {
    const userId = store.getState().user.id;
    const listings = getListings(userId);

    return {
        type: SORT_BY_POPULARITY,
        payload: {
            listings: listingsByPopularity()
        }
    }
}

export const addPreference = preference => {
    // Gets the current user from the store. Note the store import above.
    const user = store.getState().user.id;
    const updatedPreferences = addPreferenceForUser(user, preference);
    return {
        type: POPULATE_SEARCH, // Can reuse an action type if it fits
        payload: {
            listings: updatedPreferences
        }
    }
};

export const removePreference = preferences => {
    // Gets the current user from the store. Note the store import above.
    const userId = store.getState().user.id;
    const updatedPreferences = removePreferenceForUser(userId, preferences);
    return {
        type: POPULATE_SEARCH, // Can reuse an action type if it fits
        payload: {
            listings: updatedPreferences
        }
    }
};

export const saveListing = listing => {
    // Gets the current user from the store. Note the store import above.
    const userId = store.getState().user.id;
    const updatedSave = saveUserListing(userId, listing);
    return {
        type: SAVE_LISTING, // Can reuse an action type if it fits
        payload: {
            saved: updatedSave
        }
    }
};

export const completeOnboarding = () => {
    // Gets the current user from the store. Note the store import above.
    const userId = store.getState().user.id;
    userCompletedOnboarding(userId);
    return {
        type: COMPLETE_ONBOARDING
    }
}

export const logout = () => ({
    type: LOGOUT
});
