import {createStore} from "redux";
import {INITIAL_STATE, LOGIN_STATE} from "./storeConstants";
import { TOGGLE_TODO, LOGIN_SUCCESS, INVALID_LOGIN, LOGIN_NETWORK_ERROR, POPULATE_SEARCH, COMPLETE_ONBOARDING, LOGOUT } from "./actionConstants";
import { act } from "react-dom/cjs/react-dom-test-utils.production.min";

const rootReducer = (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case TOGGLE_TODO:
            return {
                ...state,
                todos: state.todos.map((todo, index) => {
                    if (index !== action.payload.id)
                        return todo;
                    return {
                        text: todo.text,
                        isComplete: !todo.isComplete
                    }
                })
            }
        case LOGIN_SUCCESS:
            return {
                ...state,
                loginState: LOGIN_STATE.LOGGED_IN,
                user: action.payload.user
            }
        case INVALID_LOGIN:
            return {...state, loginState: LOGIN_STATE.INVALID_LOGIN}
        case LOGIN_NETWORK_ERROR:
            return {...state, loginState: LOGIN_STATE.NETWORK_ERROR}
        case POPULATE_SEARCH:
            return {
                ...state,
                search: action.payload.search,
                listings: action.payload.listings
            }
        case COMPLETE_ONBOARDING:
            return {
                ...state,
                user: {
                    ...state.user,
                    onboardingComplete: true
                }
            }
        case LOGOUT:
            return INITIAL_STATE;
        default:
            return state;
    }
}

export default createStore(rootReducer);
