import {useState} from "react";
import {useDispatch} from "react-redux";
import Slide from "./Slide";
import SlideIndicator from "./SlideIndicator";
import {completeOnboarding} from "../redux/actions";

const TOTAL_SLIDES = 3;

const OnboardingSlides = () => {
    const [activeSlide, setActiveSlide] = useState(1);

    const dispatch = useDispatch();

    const setSlideState = id => {
        if (id === activeSlide)
            return "active";
        return "inactive";
    }

    const nextSlide = () => {
        let id = activeSlide;
        id < TOTAL_SLIDES ? setActiveSlide(id + 1) : dispatch(completeOnboarding());
    }

    const generateSlideIndicators = () => {
        let indicators = [];
        for (let i = 1; i <= TOTAL_SLIDES; i++) {
            indicators.push(
                <SlideIndicator slideStatus={setSlideState(i)} key={i} 
                                slideID={i} 
                                clickHandler={() => setActiveSlide(i)} />
            )
        }
        return indicators;
    }

    return (
        <div className="slides-bg container main my-5">
            <Slide slideStatus={setSlideState(1)} slideId={1}>
                <h1>Access your todo list from anywhere</h1>
                <p>Your todos automatically sync on all your devices.</p>
                <div className="fit my-4">
                    <img src={process.env.PUBLIC_URL + "/onboarding1.png"}
                        className="img-fluid my-4 float-right fit" alt="The Todo app on iPad and iPhone" />
                </div>
            </Slide>
            <Slide slideStatus={setSlideState(2)} slideId={2}>
                <h1>Filter todos by status</h1>
                <p>Decide whether you want to see all your todos, only your 
                    outstanding todos, or even just the todos you've already completed.
                </p>
                <div className="fit my-4">
                    <img src={process.env.PUBLIC_URL + "/onboarding2.gif"}
                         className="img-fluid"
                         alt="The visibility filter allows you to choose which todos are displayed." />
                </div>
            </Slide>
            <Slide slideStatus={setSlideState(3)} slideId={3}>
                <h1>Something something something</h1>
                <p>Lorem ipsum...</p>
            </Slide>
            <div className="slides-controls">
                <button className="align-left control-btn" onClick={() => dispatch(completeOnboarding())}>Skip</button>
                <div className="align-center">
                    {generateSlideIndicators()}
                </div>
                <button className="align-right control-btn" onClick={nextSlide}>
                    {
                        activeSlide < TOTAL_SLIDES ? "Next" : "Done"
                    }
                </button>
            </div>
        </div>
    )
}

export default OnboardingSlides;
