import {  useState } from "react";
import {useSelector,useDispatch} from "react-redux";
import { saveListing } from "../redux/actions";
import ListingDisplay from "./ListingDisplay";

const Listing = () => {

    const listings = useSelector(state => state.listings);
    const [current,setCurrent] = useState();
    const saved = useSelector(state => state.saved);

    const [content, setContent] = useState("")

    const updateContent = (e) => {
        console.log("content target value: ", e)
        setCurrent(listings[parseInt(e)])
        setContent(listings[parseInt(e)].description)
    }





    return(
        <div class="grid-container">
        <div class="grid-child list-group">
            {
                listings.map((listing, i) => 

                <button type="button" class={(i===0)?"list-group-item list-group-item-action active":"list-group-item list-group-item-action"} 
                aria-current={(i===0)?"true":"false"} id={i} onClick={(e) => updateContent(i)}>
                    <div class="d-flex w-100 justify-content-between">
                    <h5 class="mb-1">{listing.title}</h5>
                    <small>{listing.location}</small>
                    </div>
                    <p class="mb-1">{listing.company}</p>
                    <small>And some small print.</small>
                </button>
                )
            }
        </div>
        <div class="grid-child">
            <ListingDisplay listing={current}/>
        </div>
        </div>
    )
}

export default Listing
