import {useSelector, useDispatch} from "react-redux";
import { clearSearch, logout} from "../redux/actions";
import OnboardingSlides from "../components/OnboardingSlides";
import Search from "../components/Search"
import Saved from "../components/Saved"
import { useEffect, useState } from "react";

const Home = () => {
    const onboardingComplete = useSelector(state => state.user.onboardingComplete);
    const username = useSelector(state => state.user.username);
    const search = useSelector(state => state.search);
    const [content, setContent] = useState("Home");

    const dispatch = useDispatch();

    useEffect(() => {
        console.log("Search: ",search)
    })

    const handleLogout = () => {
        dispatch(logout());
    }

    const displayContent = (e) => {
        const current = e.target.value;
        console.log("current: ", current)
        if (current === "Search") {
            setContent("Search")
        } else if (current === "Saved") {
            setContent("Saved")
        } else {

            setContent("Home")
            dispatch(clearSearch())
        }
    }


    if (onboardingComplete) {
        return (
            <>
                <nav className="navbar navbar-dark">
                    <div className="container-fluid">
                    <label class="d-flex font">{console.username}</label>
                        <a className="navbar-brand mx-auto font title" href="#home" onClick={(e)=>{displayContent(e)}}>
                            Code for $$
                        </a>
                        <form class="d-flex">
                            <button class="btn btn-outline-light" onClick={handleLogout}>Logout</button>
                        </form>
                    </div>
                </nav>
                <ul class="nav justify-content-center">
                    <li class="nav-item">
                        <button type="button" class="nav-link active option" aria-current="page" value="Search" onClick={(e) => displayContent(e)}>Search</button>
                    </li>
                    <li class="nav-item">
                        <button type="button" class="nav-link option" value="Saved" onClick={(e) => displayContent(e)}>Saved</button>
                    </li>
                </ul>
                <div>
                    {
                        (content === "Search") ? <Search />:(content === "Saved")?<Saved />:<h1>Hi, sup?</h1>
                    }
                </div>

            </>
        )
    } else {
        return (
            <OnboardingSlides/>
        )
    }
}

export default Home
