import { useEffect, useState } from "react";
import {useSelector, useDispatch} from "react-redux";
import Listing from "./Listing";
import ReactHtmlParser from 'react-html-parser'; 

const Search = () => {

    const preferences = useSelector(state => state.search);
    const listings = useSelector(state => state.listings);
    const [content, setContent] = useState("")
    const collapseTargets = "#collapse"
    const collapseIds = "collapse"
    const itemClass = "accordion-collapse collapse"

    const updateContent = (e) => {
        console.log("content tagret value: ", e.target.value)
        setContent(listings[parseInt(e.target.value)].description)
    }

    return (
        <div class="row">
            <div class="col-4">
                <div class="list-group" id="list-tab" role="tablist">
                <a class="list-group-item list-group-item-action active" id="list-home-list" data-bs-toggle="list" href="#list-home" role="tab" aria-controls="home">Home</a>
                <a class="list-group-item list-group-item-action" id="list-profile-list" data-bs-toggle="list" href="#list-profile" role="tab" aria-controls="profile">Profile</a>
                <a class="list-group-item list-group-item-action" id="list-messages-list" data-bs-toggle="list" href="#list-messages" role="tab" aria-controls="messages">Messages</a>
                <a class="list-group-item list-group-item-action" id="list-settings-list" data-bs-toggle="list" href="#list-settings" role="tab" aria-controls="settings">Settings</a>
                </div>
            </div>
            <div class="col-8">
                <div class="tab-content" id="nav-tabContent">
                <div class="tab-pane fade show active" id="list-home" role="tabpanel" aria-labelledby="list-home-list">...</div>
                <div class="tab-pane fade" id="list-profile" role="tabpanel" aria-labelledby="list-profile-list">...</div>
                <div class="tab-pane fade" id="list-messages" role="tabpanel" aria-labelledby="list-messages-list">...</div>
                <div class="tab-pane fade" id="list-settings" role="tabpanel" aria-labelledby="list-settings-list">...</div>
                </div>
            </div>
            </div>
    )

    return(
        <div class="grid-container">
        <div class="grid-child list-group">
            {
                listings.map((listing, i) => 

                <a href={"#"+i} class={(i===0)?"list-group-item list-group-item-action active":"list-group-item list-group-item-action"} 
                aria-current={(i===0)?"true":"false"}>
                    <div class="d-flex w-100 justify-content-between">
                    <h5 class="mb-1">{listing.title}</h5>
                    <small>save</small>
                    </div>
                    <p class="mb-1">{listing.company}</p>
                    <small>And some small print.</small>
                </a>


                )
            }
        </div>
        <div class="grid-child">
            <p>{(listings.length>0)?ReactHtmlParser(content):""}</p>
        </div>
        </div>
    )



}

export default Search
