import {TOGGLE_TODO, LOGIN_SUCCESS, INVALID_LOGIN, LOGIN_NETWORK_ERROR, 
    POPULATE_TODOS, COMPLETE_ONBOARDING, LOGOUT, POPULATE_SEARCH} from "./actionConstants";
import {login, getPreferences, toggleTodoForUser, addTodoForUser, userCompletedOnboarding} from "../data/data";
import store from "./store";

export const loginSuccess = user => ({
    type: LOGIN_SUCCESS,
    payload: {
        user
    }
});

export const loginFail = () => ({
    type: INVALID_LOGIN
});

export const loginNetworkError = () => ({
    type: LOGIN_NETWORK_ERROR
});

/** The following actions get/update mock data and would have to be refactored
 * if/when the data source is changed.
 */

 // Eventually this action creator would be re-written to connect to a real data source
export const validateUser = (username, password) => {
    let user = login(username, password);
    if (user.id)
        return loginSuccess(user);
    else return loginFail();
    // No case for a network error yet
}

// Note for future: need to handle error from data source
export const getUserPreferences = () => {
    // Gets the current user from the store. Note the store import above.
    const userId = store.getState().user.search;
    return {
        type: POPULATE_SEARCH,
        payload: {
            search: getPreferences(userId)
        }
    }
}

export const toggleTodo = id => {
    // Gets the current user from the store. Note the store import above.
    const userId = store.getState().user.id;
    const updatedTodo = toggleTodoForUser(userId, id);
    return {
        type: TOGGLE_TODO,
        payload: {
            id: updatedTodo
        }
    }
};

export const updateSearch = preferences => {
    // Gets the current user from the store. Note the store import above.
    const userId = store.getState().user.id;
    const updatedPreferences = updatePreferenceForUser(userId, preferences);
    return {
        type: POPULATE_SEARCH, // Can reuse an action type if it fits
        payload: {
            todos: updatedPreferences
        }
    }
};

export const completeOnboarding = () => {
    // Gets the current user from the store. Note the store import above.
    const userId = store.getState().user.id;
    userCompletedOnboarding(userId);
    return {
        type: COMPLETE_ONBOARDING
    }
}

export const logout = () => ({
    type: LOGOUT
});

export const setPreference = () => ({
    type: PREFERENCE
})
