import Listing from "./Listing";

const Search = () => {
    const preferences = useSelector(state => state.search);
    const listings = useSelector(state => state.listings);
    const collapseTargets = "#collapse"
    const collapseIds = "collapse"
    const itemClass = "accordion-collapse collapse "

    return(
        <div className="accordion" id="accordionExample">
            {
                listings.map((listing, i) => 

                <div className="accordion-item">
                <h2 className="accordion-header" id="headingOne">
                <button className="accordion-button" type="button" data-bs-toggle="collapse" data-bs-target={collapseTargets+i} aria-expanded="true" aria-controls={collapseIds+i}>
                    {listing.title}
                </button>
                </h2>
                <div id={collapseIds+i} className={" show"} aria-labelledby="headingOne" data-bs-parent="#accordionExample">
                <div className="accordion-body">
                    <strong>{listing.company}</strong> 
                    {listing.description}
                </div>
                </div>
            </div>


                )
            }
        </div>
    )



}

export default Search
