import { useEffect, useState } from "react";
import {useSelector, useDispatch} from "react-redux";
import Listing from "./Listing";
import ReactHtmlParser from 'react-html-parser'; 

const Search = () => {

    const preferences = useSelector(state => state.search);
    const listings = useSelector(state => state.listings);
    const [content, setContent] = useState("")
    const collapseTargets = "#collapse"
    const collapseIds = "collapse"
    const itemClass = "accordion-collapse collapse"

    const updateContent = (e) => {
        console.log("content tagret value: ", e.target)
        setContent(listings[parseInt(e.target.value)].description)
    }
    /*
    return (
        <div class="row">
            <div class="col-4">
                <div class="list-group" id="list-tab" role="tablist">
                    {
                        listings.map((listing,i) => 
                        <button type="button" class={(i===0)?"btn list-group-item list-group-item-action active":"btn list-group-item list-group-item-action"} id={"list-"+i} onClick={(e) => updateContent(e)} value={i} role="tab" aria-controls="home">{listing.title}</button>
                        )
                    }
                </div>
            </div>
            <div class="col-8">
                <div class="tab-content" id="nav-tabContent">
                {
                        listings.map((listing,i) => 
                        <div class={(i===0)?"tab-pane fade show active":"tab-pane fade"} id={"job"+i} role="tabpanel" aria-labelledby={"list-"+i}>{ReactHtmlParser(listing.description)}</div>
                        )
                    }
                </div>
            </div>
            </div>
    )*/

    return(
        <div class="grid-container">
        <div class="grid-child list-group">
            {
                listings.map((listing, i) => 

                <button type="button" class={(i===0)?"list-group-item list-group-item-action active":"list-group-item list-group-item-action"} 
                aria-current={(i===0)?"true":"false"} value={i} onClick={(e) => updateContent(e)}>
                    <div class="d-flex w-100 justify-content-between">
                    <h5 class="mb-1">{listing.title}</h5>
                    <small>save</small>
                    </div>
                    <p class="mb-1">{listing.company}</p>
                    <small>And some small print.</small>
                </button>


                )
            }
        </div>
        <div class="grid-child">
            <p>{(listings.length>0)?ReactHtmlParser(content):""}</p>
        </div>
        </div>
    )



}

export default Search
