import { useEffect, useState } from "react";
import {useSelector, useDispatch} from "react-redux";
import Listing from "./Listing";
import ReactHtmlParser from 'react-html-parser'; 

const Search = () => {

    const preferences = useSelector(state => state.search);
    const listings = useSelector(state => state.listings);
    const [content, setContent] = useState("")
    const collapseTargets = "#collapse"
    const collapseIds = "collapse"
    const itemClass = "accordion-collapse collapse"

    const updateContent = (e) => {
        console.log("content tagret value: ", e.target.value)
        setContent(listings[parseInt(e.target.value)].description)
    }

    return (
        <div class="row">
            <div class="col-4">
                <div class="list-group" id="list-tab" role="tablist">
                    {
                        listings.map((listing,i) => 
                        <a class={(i===0)?"list-group-item list-group-item-action active":"list-group-item list-group-item-action"} id={"list-"+i} data-bs-toggle="list" href={"#job"+i} onClick={() => updateContent()} role="tab" aria-controls="home">{listing.title}</a>
                        )
                    }
                </div>
            </div>
            <div class="col-8">
                <div class="tab-content" id="nav-tabContent">
                {
                        listings.map((listing,i) => 
                        <div class={(i===0)?"tab-pane fade show active":"tab-pane fade"} id={"job"+i} role="tabpanel" aria-labelledby={"list-"+i}>{ReactHtmlParser(listing.description)}</div>
                        )
                    }
                </div>
            </div>
            </div>
    )
